import { Component } from '@angular/core';

@Component({
  selector: 'binding',
  templateUrl: './binding.component.html',
  styleUrls: ['./binding.component.css']
})
export class BindingComponent {
  //variabile utilizzata per l'interpolazione di stringa
  numTests: number = 25;

  //oggetto utilizzato per le proprietà
  car = {
    model: "Alfa Romeo Giulia",
    img:"https://upload.wikimedia.org/wikipedia/commons/7/74/Alfa_Romeo_Giulia_Super.jpg",
    available:false
  }

  // Variabili utilizzate per la proprietà vincolante class
  myClass:string = "red";
  setBlueClass:boolean = false;

  // Variabili utilizzate per la direttiva ngClass
  componentClasses = {
    show: true,
    warning: false
  }
  formOk:boolean = false;
  admin:boolean = false;

  // Oggetto utilizzato per la direttiva ngStyle
  componentStyle = {
    'width':'300px',
    'height.px':100,
    'backgroundColor':'forestgreen'
  }

  // Variabile utilizzata per "Two-way binding" con [(ngModel)]
  textSize:number = 20;
  username: string = "";
  voto:number=6;

  //Vetore utilizzato per la direttiva ngFor
  ingredients=[
    'avocado',
    'pomodoro',
    'lattuga',
    'maionese',
    'pancetta',
    'pane',
    'birra'
  ];

  //per generarlo automaticamente, premere alt invio sul richiamo all'evento
  getClick() {
    console.log("Btn clicked!");
    this.car.available = !this.car.available;
    this.setBlueClass = !this.setBlueClass;
    this.myClass = "blue";
    this.formOk = !this.formOk;
    this.admin = !this.admin;
  }

  inviaDati() {
    console.log("Nome utente inserito: "+this.username);
  }

  getColor() {
    if(this.voto>=6)
      return "lightgreen";
    else
      return "red";
  }
}
<div class="row">
  <div class="col-sm-6 bg-warning">
    <h3>Beta testing</h3>
    <h4>Numero di test eseguiti: {{numTests}}</h4> <!--"one way binding", ovvero a senso unico (da ts a html)-->
  </div>
  <div class="col-sm-6 bg-light">
    <h3>Confronto tra interpolazione di stringa e proprietà vincolante</h3>
    <h4>Interpolazione di stringa: <span class="text-primary">{{car.model}}</span></h4>
    <h4>Proprietà vincolante: <span class="text-danger" [textContent]="car.model"></span></h4>
    <hr width="70%">
    <div><img src="{{car.img}}" alt="NO IMAGE"></div>
    <div><img [src]="car.img" alt="NO IMAGE"></div>
  </div>

</div>

<div class="row">
  <div class="col-sm-6 bg-info bg-opacity-50 p-3">
    <h3>Test evento click su un button</h3>
    <button class="btn btn-primary" (click)="getClick()">Test Button</button>
  </div>
  <div class="col-sm-6">
    <h3>Vendita dell'auto</h3>
    <button class="btn btn-dark" [disabled]="!car.available">Vendi Auto</button>
  </div>
</div>

<div class="row">
  <div class="col-sm-6 bg-success">
    <h3>Binding delle classi CSS</h3>
    <p [class]="myClass">Testo formattato in rosso</p>
    <p [class.blue]="setBlueClass">Testo scritto in blu...forse</p>
  </div>
  <div class="col-sm-6 bg-dark text-white">
    <h3>Direttiva ngClass</h3>
    <p [ngClass]="componentClasses">Paragrafo formattato tramite ngClass</p>
    <p [ngClass]="{
      'cl1':!formOk,
      'cl2':formOk && admin
    }">Paragrafo formattato dinamicamente</p>
    <p>formOk: {{formOk}}</p>
    <p>admin: {{admin}}</p>
  </div>
</div>

<div class="row">
  <div class="col-sm-6 bg-warning">
    <h3>Direttiva ngStyle</h3>
    <p [ngStyle]="componentStyle">Paragrafo formattato con direttiva ngStyle</p>
  </div>
  <div class="col-sm-6">
    <p><input type="color" #myColor></p>
    <p><input type="range" [(ngModel)]="textSize" min="0" max="1000"></p>
    <h5>Valore di textSize: {{textSize}}</h5>
    <p [ngStyle]="{'font-size':textSize+'px', 'color':myColor.value}">
      Testo modificato dinamicamente in dimensioni e colore
    </p>
  </div>
</div>

<div class="row">
  <div class="col-sm-6">
    <h3>Two way binding con direttiva ngModel</h3>
    <p>Inserire nome utente: <input type="text" [(ngModel)]="username"></p>
    <p>Inserire il voto: <input type="number" [(ngModel)]="voto">
      <span style="color: red" *ngIf="voto > 10 || voto < 2">Il voto inserito non è valido</span>
      <!--se ngIf è true allora l'elemento viene visualizzato, altrimenti no-->
    </p>
    <p><button class="btn btn-success" (click)="inviaDati()">Invia dati</button></p>
    <p>Username inserito: {{username}}</p>
    <p [style.background-color]="getColor()" class="m-4">Voto registrato: {{voto}}</p>
  </div>
  <div class="col-sm-6 bg-dark text-white">
    <h3>Direttiva ngFor</h3>
    <h4>Lista degli ingredienti</h4>
    <div *ngFor="let item of ingredients;
                  let i=index">
      <!--il div è duplicato sul dom per ogni elemento del vettore-->
      {{i+1}} - {{item}}
    </div>
  </div>
</div>
